import streamlit as st
import asyncio
from SessionState import get
from httpx_oauth.clients.google import GoogleOAuth2
import os
from dotenv import load_dotenv
load_dotenv()

CLIENT_ID=os.getenv("CLIENT_ID")
CLIENT_SECRET=os.getenv("CLIENT_SECRET")

async def write_authorization_url(client,
                                  redirect_uri):
    authorization_url = await client.get_authorization_url(
        redirect_uri,
        scope=["profile", "email"],
        extras_params={"access_type": "offline"},
    )
    return authorization_url


async def write_access_token(client,
                             redirect_uri,
                             code):
    token = await client.get_access_token(code, redirect_uri)
    return token


async def get_email(client,
                    token):
    user_id, user_email = await client.get_id_email(token)
    return user_id, user_email


def main(user_id, user_email):
    st.sidebar.write(f"You're logged in as {user_email}")



# if __name__ == '__main__':
def authorize():
    client_id = CLIENT_ID
    client_secret = CLIENT_SECRET
    redirect_uri = 'http://localhost:8501/'

    client = GoogleOAuth2(client_id, client_secret)
    authorization_url = asyncio.run(
        write_authorization_url(client=client,
                                redirect_uri=redirect_uri)
    )

    session_state = get(token=None)
    if session_state.token is None:
        try:
            code = st.experimental_get_query_params()['code']
        except:
            st.sidebar.write(f'''<h1>
                     <a target="_self"
                    href="{authorization_url}"><img src='data:image/png;base64,'width="270"height="50"></a></h1>''',
                             unsafe_allow_html=True)
        else:
            # Verify token is correct:
            try:
                token = asyncio.run(
                    write_access_token(client=client,
                                       redirect_uri=redirect_uri,
                                       code=code))


            except:
                st.sidebar.write(f'''<h1>
                        Session Expired ReLogin <a target="_self"
                        href="{authorization_url}"><img src='data:image/png;base64,'width="200"height="50"></a></h1>''',
                         unsafe_allow_html=True)

            else:
                # Check if token has expired:
                if token.is_expired():
                    if token.is_expired():
                        st.write(f'''<h1>
                            Login session has ended,
                            please <a target="_self" href="{authorization_url}">
                            login</a> again.</h1>
                            ''')
                else:
                    session_state.token = token
                    user_id, user_email = asyncio.run(
                        get_email(client=client,
                                  token=token['access_token'])
                    )
                    session_state.user_id = user_id
                    session_state.user_email = user_email
                    main(user_id=session_state.user_id,
                         user_email=session_state.user_email)
                    return 2

    else:
        main(user_id=session_state.user_id,
             user_email=session_state.user_email)
        return 2
